          }
        };

        handlers.usermove = function(x, y) {
          var sub = self.getObj(activeSubscene),
              code = sub.callbacks[drag].update;
          if (code) {
            var fn = Function('"use strict";return (' + code + ')')();
            fn.call(self, x, y);
          }
