            ib = [[0, 2, 3, 1], 
                  [2, 6, 7, 3], 
                  [1, 3, 7, 5], 
                  [0, 4, 6, 2], 
                  [0, 1, 5, 4], 
                  [4, 5, 7, 6]], 
            centers = [], i, j, k, 
            i0, i1, i2,
            normal, result = {};

        for (i = 0; i < ib.length; i++) {
          centers.push([0,0,0]);
          for (j = 0; j < 3; j++) { // x,y,z
            for (k = 0; k < 4; k++) {// vertices
