            locations = this.R_pretty(limits[0], limits[1], 5,
                                    2, // min_n
                                    0.75, // shrink_sml
                                    [1.5, 2.75], // high_u_fact
                                    0, // eps_correction
                                    0); // return_bounds)  
            for (i = locations.lo; i <= locations.up; i++) {
              value = (i*locations.unit - limits[0])/range;
              if (0 < value && value < 1)
                result[dim].push(value);
