      this.m12 /= det;
      this.m13 /= det;
      this.m14 /= det;

      this.m21 /= det;
      this.m22 /= det;
      this.m23 /= det;
      this.m24 /= det;

      this.m31 /= det;
      this.m32 /= det;
      this.m33 /= det;
      this.m34 /= det;

