      var ty = (top + bottom) / (bottom - top);
      var tz = (far + near) / (near - far);

      var matrix = new CanvasMatrix4();
      matrix.m11 = 2 / (right - left);
      matrix.m12 = 0;
      matrix.m13 = 0;
      matrix.m14 = 0;
      matrix.m21 = 0;
      matrix.m22 = 2 / (top - bottom);
      matrix.m23 = 0;
      matrix.m24 = 0;
      matrix.m31 = 0;
      matrix.m32 = 0;
      matrix.m33 = -2 / (far - near);
      matrix.m34 = 0;
      matrix.m41 = tx;
      matrix.m42 = ty;
      matrix.m43 = tz;
      matrix.m44 = 1;

      this.multRight(matrix);
