  }
  .storyboard-nav button:disabled,
  .storyboard-nav button:disabled {
  opacity: .1;
  }
  .storyboard-nav .sbnext,
  .storyboard-nav .sbprev {
  float: left;
  width: 2%;
  height: 120px;
  }
  .storyboard-nav .sbprev {
  text-align: left;
  width: 2%;
  }
  .storyboard-nav .sbnext {
  float: right;
  text-align: right;
  margin-right: 8px;
  }
  .storyboard-nav .sbframelist {
  margin: 0 auto;
  width: 94%;
  height: 120px;
  overflow: hidden;
  text-shadow: none;
  margin-bottom: 8px;
  }
  .storyboard-nav .sbframelist ul {
  list-style: none;
  margin: 0;
  padding: 0;
  height: 100%;
  }
  .storyboard-nav .sbframelist ul li {
  float: left;
  width: 270px;
  height: 100%;
  padding: 10px 10px 10px 10px;
  margin-right: 8px;
  text-align: left;
  cursor: pointer;
  }
  .storyboard-nav .sbframelist ul li:last-child {
  margin-right: 0px;
  }
  .sbframe-commentary {
  width: 300px;
  }
  .sbframe-commentary ul {
  padding-left: 22px;
  }
  .sbframe.active {
  display: flex;
  }
  .sbframe:not(.active) {
  display: none;
  }
  @media only screen and (min-width: 768px) {
  html, body {
  height: 100%;
  }
  #dashboard-container {
  height: 100%;
  }
  }
