        }
      }

      // check for a valueOutputSpan
      if (valueOutputSpan.length > 0) {
        handleValueOutput(valueOutputSpan);
      }

      // if we have a shinyOutput then bind a listener to handle
      // new valueOutputSpan values
      shinyOutput.on('shiny:value',
